import React from "react";
import styles from "./PanelButtons.module.css";

import InputCheckbox from "../InputCheckbox/InputCheckbox";
import { EngineProps } from "../../../model/Engine";
import { PowerPlantProps } from "../../../model/PowerPlant";
import { ShieldProps } from "../../../model/Shield";

type Props = {
    engineProps: EngineProps,
    powerPlantProps: PowerPlantProps,
    shieldProps: ShieldProps
}

const PanelButtons: React.FC<Props> = ({engineProps, powerPlantProps, shieldProps}) => {
    return(
        <div className={styles.pb_container} >
            <div className={styles.pb_leftContainer} >...</div>

            <div className={styles.pb_rightContainer} >
                <div className={styles.pb_switch} >
                    <InputCheckbox checked={powerPlantProps.powerStatus} />
                </div>

                <div className={styles.pb_switch} >
                    <InputCheckbox checked={engineProps.powerStatus} />
                </div>

                <div className={styles.pb_switch} >
                    <InputCheckbox checked={shieldProps.powerStatus} />
                </div>
            </div>
        </div>
    );
}

export default PanelButtons;
