import React from "react";
import styles from "./PanelInfo.module.css";

import PowerPlantInfo from "./PowerPlantInfo/PowerPlantInfo";
import { PowerPlantProps } from "../../../model/PowerPlant";

import EngineInfo from "./EngineInfo/EngineInfo";
import { EngineProps } from "../../../model/Engine";

import ShieldInfo from "./ShieldInfo/ShieldInfo";
import { ShieldProps } from "../../../model/Shield";

const PanelInfo: React.FC = () => {

        const [powerPlantProps, setPowerPlantProps] = React.useState<PowerPlantProps>( PowerPlantInitialProps );
        const [engineProps, setEngineProps] = React.useState<EngineProps>( EngineInitialProps );
        const [shieldProps, setShieldProps] = React.useState<ShieldProps>( ShieldInitialProps );

        const fetchFunctionMyTest = async () => {
            await fetch('http://localhost:8080/ship_status')
                .then((response) => response.json())
                .then((data) => {
                    setShieldProps(data.shieldDto);
                    setEngineProps(data.engineDto);
                    setPowerPlantProps(data.powerPlantDto);
                }).catch((err) => {
                    console.log(err.message);
                });
        };

        React.useEffect(() => {
            const interval = setInterval(() => {
              fetchFunctionMyTest();
            }, 1000);
            return () => clearInterval(interval);
          }, []);

    return(
        <div className={styles.pi_container} >
            <PowerPlantInfo powerPlantProps={powerPlantProps} />
            <EngineInfo engineProps={engineProps} />
            <ShieldInfo shieldProps={shieldProps} />
        </div>
    );
}

export default PanelInfo;
