import React from "react";
import styles from "./PanelButtons.module.css";

import InputCheckbox from "../InputCheckbox/InputCheckbox";
import { EngineProps } from "../../../model/Engine";
import { PowerPlantProps } from "../../../model/PowerPlant";
import { ShieldProps } from "../../../model/Shield";

type Props = {
    engineProps: EngineProps,
    powerPlantProps: PowerPlantProps,
    shieldProps: ShieldProps,

    changeEngine(param: boolean): void,
    changePowerPlant(param: boolean): void,
    changeShield(param: boolean): void
}

const PanelButtons: React.FC<Props> = ({engineProps, powerPlantProps, shieldProps, changeEngine, changePowerPlant, changeShield}) => {
    return(
        <div className={styles.pb_container} >
            <div className={styles.pb_leftContainer} >...</div>

            <div className={styles.pb_rightContainer} >
                <div className={styles.pb_switch} >
                    <InputCheckbox checked={powerPlantProps.powerStatus} onChange={changePowerPlant}/>
                </div>

                <div className={styles.pb_switch} >
                    <InputCheckbox checked={engineProps.powerStatus} onChange={changeEngine} />
                </div>

                <div className={styles.pb_switch} >
                    <InputCheckbox checked={shieldProps.powerStatus} onChange={changeShield} />
                </div>
            </div>
        </div>
    );
}

export default PanelButtons;
