import React from "react";
import styles from "./PanelShipStatus.module.css";

import { PowerPlantProps, PowerPlantInitialProps } from "../../../model/PowerPlant";
import { EngineProps, EngineInitialProps } from "../../../model/Engine";
import { ShieldProps, ShieldInitialProps } from "../../../model/Shield";

const LogMessage: React.FC = () => {
    const [powerPlantProps, setPowerPlantProps] = React.useState<PowerPlantProps>( PowerPlantInitialProps );
    const [engineProps, setEngineProps] = React.useState<EngineProps>( EngineInitialProps );
    const [shieldProps, setShieldProps] = React.useState<ShieldProps>( ShieldInitialProps );

    const fetchFunctionMyTest = async () => {
        await fetch('http://localhost:8080/ship_status')
            .then((response) => response.json())
            .then((data) => {
                setShieldProps(data.shieldDto);
                setEngineProps(data.engineDto);
                setPowerPlantProps(data.powerPlantDto);
            }).catch((err) => {
                console.log(err.message);
            });
    };

    React.useEffect(() => {
        const interval = setInterval(() => {
          fetchFunctionMyTest();
        }, 1000);
        return () => clearInterval(interval);
      }, []);

    return (
        <div className={styles.lm_container} >
            <div className={styles.lm_message}>Shield Status:
                {shieldProps.powerStatus==true?"shield on":"shield off"}
                {shieldProps.capacity}
                {shieldProps.powerConsumption}
            </div>
            <div className={styles.lm_message}>Powerplant Status:
                {powerPlantProps.powerStatus==true?"Powerplant on":"Powerplant off"}
                {powerPlantProps.energyAvailable}
            </div>
            <div className={styles.lm_message}>Engine Status:
                {engineProps.powerStatus==true?"Engine on":"Engine off"}
                {engineProps.energyAvailable}
            </div>
        </div>
    );
}

export default LogMessage;
